export function summarizeIndex(storyIndex) {
  var storyCount = 0;
  var docsPageCount = 0;
  var storiesMdxCount = 0;
  var mdxCount = 0;
  Object.values(storyIndex.entries).forEach(function (entry) {
    if (entry.type === 'story') {
      storyCount += 1;
    } else if (entry.type === 'docs') {
      if (entry.standalone) {
        mdxCount += 1;
      } else if (entry.importPath.endsWith('.mdx')) {
        storiesMdxCount += 1;
      } else {
        docsPageCount += 1;
      }
    }
  });
  return {
    storyCount: storyCount,
    docsPageCount: docsPageCount,
    storiesMdxCount: storiesMdxCount,
    mdxCount: mdxCount,
    version: storyIndex.v
  };
}